package corejava;

public class Twotablewhileloop {

	public static void main(String[] args) {
		// TODO Auto-generated method stub
		
		int N = 2 ;
		
		int range =10;
		
		int i = 1;
			
			while (i <=range)
		{
			
			System.out.println(N + "*" + i + "=" + N * i);
			
			i++;
		}

}

}